import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card } from "@/components/ui/card";
import { toast } from "@/hooks/use-toast";
import heroImage from "@/assets/dashboard-hero.jpg";
import cmlensLogo from "@/assets/cmlens-eye-logo.webp";
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Simulate login process
    await new Promise(resolve => setTimeout(resolve, 1000));
    if (username === "admin" && password === "admin") {
      localStorage.setItem("cmlens_session", "authenticated");
      toast({
        title: "Welcome to CMLens",
        description: "Successfully logged in"
      });
      navigate("/upload");
    } else {
      toast({
        title: "Login Failed",
        description: "Invalid credentials. Use admin/admin",
        variant: "destructive"
      });
    }
    setIsLoading(false);
  };
  return <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <div className="w-full max-w-6xl grid lg:grid-cols-2 gap-8 items-center">
        {/* Hero Section */}
        <div className="hidden lg:block">
          <div className="glass-card p-8">
            <img src={heroImage} alt="CMLens Dashboard Preview" className="w-full h-96 object-cover rounded-xl" />
            <div className="mt-6 space-y-4">
              <div className="flex items-center space-x-3">
                <img src={cmlensLogo} alt="CMLens Logo" className="h-12 w-12" />
                <h1 className="text-4xl font-bold bg-gradient-to-r from-primary to-primary-hover bg-clip-text text-transparent">
                  CMLens
                </h1>
              </div>
              <p className="text-lg text-muted-foreground">Clear, actionable views of agent performanceâ€”at a glance.</p>
              <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-success rounded-full"></div>
                  <span>Upload your dataâ€”get instant performance clarity.</span>
                </div>

              </div>
            </div>
          </div>
        </div>

        {/* Login Form */}
        <div className="w-full max-w-md mx-auto">
          <Card className="glass-card p-8">
            <div className="space-y-6">
              <div className="text-center space-y-2">
                <h2 className="text-3xl font-bold">Welcome Back</h2>
                <p className="text-muted-foreground">
                  Sign in to access your dashboard
                </p>
              </div>

              <form onSubmit={handleLogin} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="username">Username</Label>
                  <Input id="username" type="text" placeholder="Enter username" value={username} onChange={e => setUsername(e.target.value)} required className="glass-card" />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input id="password" type="password" placeholder="Enter password" value={password} onChange={e => setPassword(e.target.value)} required className="glass-card" />
                </div>

                <Button type="submit" className="w-full btn-hero" disabled={isLoading}>
                  {isLoading ? "Signing in..." : "Sign In"}
                </Button>
              </form>

              <div className="text-center text-sm text-muted-foreground">
                Demo credentials: <strong>admin / admin</strong>
              </div>
            </div>
          </Card>
        </div>
      </div>
    </div>;
};
export default Login;

