src/components/AgentDetailModal.tsx.content:
import React from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { Target, Users, TrendingUp, TrendingDown, CheckCircle, AlertTriangle, ExternalLink } from "lucide-react";
import { Button } from "@/components/ui/button";

interface AgentData {
  id: string;
  name: string;
  team: string;
  group: string;
  students: number;
  ccPct: number | null;
  scPct: number | null;
  upPct: number | null;
  fixedPct: number | null;
  referralLeads: number;
  referralShowups: number;
  referralPaid: number;
}

interface AgentDetailModalProps {
  isOpen: boolean;
  onClose: () => void;
  agent: AgentData | null;
}

const AgentDetailModal: React.FC<AgentDetailModalProps> = ({ isOpen, onClose, agent }) => {
  if (!agent) return null;

  // Get targets from localStorage
  const targets = React.useMemo(() => {
    const saved = localStorage.getItem("cmlens_targets");
    return saved ? JSON.parse(saved) : {
      classConsumption: 80,
      superClassConsumption: 15,
      upgradeRate: 25,
      fixedRate: 60,
    };
  }, []);

  // Helper function to get status vs target
  const getStatus = (value: number | null, target: number) => {
    if (value === null) return "na";
    if (value >= target) return "above";
    if (value >= target * 0.9) return "warning";
    return "below";
  };

  // Function to count targets achieved
  const countTargetsAchieved = (agent: AgentData) => {
    let achieved = 0;
    if (getStatus(agent.fixedPct, targets.fixedRate) === "above") achieved++;
    if (getStatus(agent.ccPct, targets.classConsumption) === "above") achieved++;
    if (getStatus(agent.scPct, targets.superClassConsumption) === "above") achieved++;
    if (getStatus(agent.upPct, targets.upgradeRate) === "above") achieved++;
    return achieved;
  };

  // Calculate overall score (weighted average)
  const calculateOverallScore = (agent: AgentData) => {
    const scores = [];
    if (agent.fixedPct !== null) scores.push((agent.fixedPct / targets.fixedRate) * 100);
    if (agent.ccPct !== null) scores.push((agent.ccPct / targets.classConsumption) * 100);
    if (agent.scPct !== null) scores.push((agent.scPct / targets.superClassConsumption) * 100);
    if (agent.upPct !== null) scores.push((agent.upPct / targets.upgradeRate) * 100);

    return scores.length > 0 ? scores.reduce((a, b) => a + b, 0) / scores.length : 0;
  };

  const formatPercent = (value: number | null) => {
    return value !== null ? `${value.toFixed(1)}%` : "N/A";
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "above": return "text-green-600 bg-green-100";
      case "warning": return "text-yellow-600 bg-yellow-100";
      case "below": return "text-red-600 bg-red-100";
      default: return "text-gray-600 bg-gray-100";
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "above": return <CheckCircle className="h-4 w-4" />;
      case "warning": return <AlertTriangle className="h-4 w-4" />;
      case "below": return <TrendingDown className="h-4 w-4" />;
      default: return <Target className="h-4 w-4" />;
    }
  };

  const metrics = [
    {
      label: "Fixed Rate",
      value: agent.fixedPct,
      target: targets.fixedRate,
      icon: <Target className="h-4 w-4" />
    },
    {
      label: "Class Consumption",
      value: agent.ccPct,
      target: targets.classConsumption,
      icon: <TrendingUp className="h-4 w-4" />
    },
    {
      label: "Super Class",
      value: agent.scPct,
      target: targets.superClassConsumption,
      icon: <Users className="h-4 w-4" />
    },
    {
      label: "Upgrade Rate",
      value: agent.upPct,
      target: targets.upgradeRate,
      icon: <TrendingUp className="h-4 w-4" />
    }
  ];

  const targetsAchieved = countTargetsAchieved(agent);
  const overallScore = calculateOverallScore(agent);

  const openTrainingRecord = () => {
    window.open(`https://cm-dashboard.replit.app/?agent=${encodeURIComponent(agent.id)}`, '_blank');
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            <span className="text-2xl font-bold">{agent.id}</span>
            <div className="flex items-center gap-2">
              <Badge variant="outline" className="px-3 py-1">
                {targetsAchieved}/4 Targets Achieved
              </Badge>
              <Button
                variant="outline"
                size="sm"
                onClick={openTrainingRecord}
                className="flex items-center gap-2"
              >
                <ExternalLink className="h-4 w-4" />
                Training Record
              </Button>
            </div>
          </DialogTitle>
          <DialogDescription>
            {agent.team} â€¢ {agent.group} â€¢ {agent.students} Students
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Overall Performance Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Performance Overview</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center">
                  <div className="text-3xl font-bold text-primary">
                    {overallScore.toFixed(1)}%
                  </div>
                  <div className="text-sm text-muted-foreground">Overall Score</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-green-600">
                    {targetsAchieved}
                  </div>
                  <div className="text-sm text-muted-foreground">Targets Achieved</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold text-blue-600">
                    {agent.students}
                  </div>
                  <div className="text-sm text-muted-foreground">Students</div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Detailed Metrics Breakdown */}
          <Card>
            <CardHeader>
              <CardTitle>Target Achievement Breakdown</CardTitle>
              <CardDescription>
                Detailed analysis of performance vs targets with explanations
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {metrics.map((metric) => {
                  const status = getStatus(metric.value, metric.target);
                  const progress = metric.value !== null ? Math.min((metric.value / metric.target) * 100, 100) : 0;

                  return (
                    <div key={metric.label} className="space-y-2">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          {metric.icon}
                          <span className="font-medium">{metric.label}</span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Badge className={`${getStatusColor(status)} border-0`}>
                            {getStatusIcon(status)}
                            {status === "above" ? "Above Target" : 
                             status === "warning" ? "Near Target" : 
                             status === "below" ? "Below Target" : "No Data"}
                          </Badge>
                          <span className="font-bold">
                            {formatPercent(metric.value)} / {metric.target}%
                          </span>
                        </div>
                      </div>
                      <Progress value={progress} className="h-2" />
                      <div className="flex justify-between text-xs text-muted-foreground">
                        <span>Current: {formatPercent(metric.value)}</span>
                        <span>Target: {metric.target}%</span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>

          {/* Referral Performance */}
          <Card>
            <CardHeader>
              <CardTitle>Referral Performance</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-3 gap-4 text-center">
                <div>
                  <div className="text-2xl font-bold text-blue-600">
                    {agent.referralLeads}
                  </div>
                  <div className="text-sm text-muted-foreground">Leads Generated</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-green-600">
                    {agent.referralShowups}
                  </div>
                  <div className="text-sm text-muted-foreground">Showups</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-purple-600">
                    {agent.referralPaid}
                  </div>
                  <div className="text-sm text-muted-foreground">Paid Conversions</div>
                </div>
              </div>

              {agent.referralLeads > 0 && (
                <>
                  <Separator className="my-4" />
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-muted-foreground">Showup Rate:</span>
                      <span className="font-medium ml-2">
                        {((agent.referralShowups / agent.referralLeads) * 100).toFixed(1)}%
                      </span>
                    </div>
                    <div>
                      <span className="text-muted-foreground">Conversion Rate:</span>
                      <span className="font-medium ml-2">
                        {agent.referralShowups > 0 
                          ? ((agent.referralPaid / agent.referralShowups) * 100).toFixed(1)
                          : '0.0'}%
                      </span>
                    </div>
                  </div>
                </>
              )}
            </CardContent>
          </Card>

          {/* Ranking Explanation */}
          <Card>
            <CardHeader>
              <CardTitle>Ranking Explanation</CardTitle>
              <CardDescription>
                Why this agent has this ranking and what needs improvement
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex items-center gap-2">
                  <Badge variant="outline">Rank by Targets: {targetsAchieved}/4</Badge>
                  <Badge variant="outline">Overall Score: {overallScore.toFixed(1)}%</Badge>
                </div>

                <div className="space-y-2">
                  <h4 className="font-medium text-green-600">Strengths:</h4>
                  <ul className="space-y-1 text-sm">
                    {metrics.filter(m => getStatus(m.value, m.target) === "above").map(m => (
                      <li key={m.label} className="flex items-center gap-2">
                        <CheckCircle className="h-3 w-3 text-green-600" />
                        {m.label}: {formatPercent(m.value)} (Target: {m.target}%)
                      </li>
                    ))}
                    {metrics.filter(m => getStatus(m.value, m.target) === "above").length === 0 && (
                      <li className="text-muted-foreground">No targets currently exceeded</li>
                    )}
                  </ul>
                </div>

                <div className="space-y-2">
                  <h4 className="font-medium text-red-600">Areas for Improvement:</h4>
                  <ul className="space-y-1 text-sm">
                    {metrics.filter(m => getStatus(m.value, m.target) === "below").map(m => (
                      <li key={m.label} className="flex items-center gap-2">
                        <TrendingDown className="h-3 w-3 text-red-600" />
                        {m.label}: {formatPercent(m.value)} (Need: {m.target}% - Gap: {m.value !== null ? (m.target - m.value).toFixed(1) : m.target}%)
                      </li>
                    ))}
                    {metrics.filter(m => getStatus(m.value, m.target) === "below").length === 0 && (
                      <li className="text-muted-foreground">All available targets are being met!</li>
                    )}
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default AgentDetailModal;

